interface Destroyable {
  isDestroyed(): boolean;
  destroy(): void;
}

function isDestroyable(d: any): d is Destroyable {
  return d && typeof d.isDestroyed === "function" && typeof d.destroy === "function";
}

function isDestroyed(d: any) {
  return isDestroyable(d) && d.isDestroyed();
